import { useSuperHeroes } from "../hooks/useSuperHeroes";

const RQSuperHeroes = () => {

  const onSuccess = (data) =>{
    console.log("Data fetching Success", data);
  }
  const onError = (data) =>{
    console.log("Data Fetching Error", data);
  }

  const { isLoading, data, isError, error, isFetching, refetch } = useSuperHeroes(onSuccess, onError)

  if (isLoading || isFetching) {
    return <h2>Loading...</h2>;
  }
  if (isError) {
    return <h2>{error.message}</h2>;
  }

  const handleRefetch = () => {
    // Use the refetch function with the `force` option set to true
    refetch({ enable: false });
  };

  return (
    <>
      <h2>Fetch Heroes</h2>
      <button onClick={() => handleRefetch}>Refetch</button>
      {/* {data?.data.map((hero) => {
        return <div key={hero.name}>{hero.name}</div>;
      })} */}
      {data.map((heroName)=> {
        return <div key={heroName}>{heroName}</div>;
      })}
    </>
  );
};

export default RQSuperHeroes;
