import { useSuperHeroes } from "../hooks/useSuperHeroes";

const RQSuperHeroesFetch = () => {

  const onSuccess = (data) =>{
    console.log("Data fetching Success", data);

  }
  const onError = (data) =>{
    console.log("Data Fetching Error", data);
  }
  const dataFetch = useSuperHeroes();
  const { isLoading, data, isError, error, isFetching, refetch } = useSuperHeroes(
    onSuccess, 
    onError);

    const handleFetch = () => {
        // Enable the query and trigger fetching when the button is clicked
        refetch({ force: true, enabled: true });
    }

  if (isLoading || isFetching) {
    return <h2>Loading...</h2>;
  }
  if (isError) {
    return <h2>{error.message}</h2>;
  }
  console.log("Loading...",dataFetch);
  return (
    <>
      <h2>Fetch Heroes</h2>
      <button onClick={refetch}>Refetch</button>
      {/* {data?.data.map((hero) => {
        return <div key={hero.name}>{hero.name}</div>;
      })} */}
      {data?.map((heroName)=> {
        return <div key={heroName}>{heroName}</div>;
      })}
    </>
  );
};

export default RQSuperHeroesFetch;
