import React from 'react';
import { useSuperHeroes } from '../hooks/useSuperHeroes';

const RQSuperHeroes = () => {
  const onSuccess = (data) => {
    console.log('Data fetching Success', data);
  };
  const onError = (data) => {
    console.log('Data Fetching Error', data);
  };

  // Set enabled to false initially
  const { isLoading, data, isError, error, isFetching, refetch } = useSuperHeroes(
    onSuccess,
    onError,
    {
      enabled: false, // Initially disabled
    }
  );

  const handleFetch = () => {
    // Enable the query and trigger fetching when the button is clicked
    refetch({ force: true, enabled: true });
  };

  return (
    <>
      <h2>Fetch Heroes</h2>
      <button onClick={handleFetch}>Fetch Data</button>
      {isLoading || isFetching ? (
        <h2>Loading...</h2>
      ) : isError ? (
        <h2>{error.message}</h2>
      ) : (
        <>
          <button onClick={() => refetch({ force: true })} disabled={!data}>
            Refetch
          </button>
          {data ? (
            data.map((heroName) => (
              <div key={heroName}>{heroName}</div>
            ))
          ) : (
            <div>No heroes available.</div>
          )}
        </>
      )}
    </>
  );
};

export default RQSuperHeroes;
