import { useQuery } from'react-query';
import axios from 'axios';

const fetchHeroes = () => {
  const response = axios.get("http://localhost:4000/superheroes");
  return response;
};

export const useSuperHeroes = (onSuccess, onError) => {
    useQuery(
        "super-heroes",
        fetchHeroes,
        {
          onSuccess,
          onError,
          select : (data) =>{
            const superHeroes = data.data.map((hero) =>hero.name);
            return superHeroes;
          },
        }
    )
}
